import React, {Component} from 'react';
  
class ToDo extends Component {
    
    state={ 
       datas:[
           {id:1,name:"NodeJs and express", status:true},
           {id:2,name:"React and express", status:true},
           {id:3,name:"Mongo and Node", status:true},
           {id:4,name:"fluter and firebase", status:false},
       ]
    };

    Add = () => {
        const data = [{id:this.state.datas.length +1, name:"new to do" }]
        const datas = [...this.state.datas, ...data];
        this.setState({datas})
    };

    delete = (id) => {
        const datas = this.state.datas.filter(d => d.id !== id);
        this.setState({datas});
    };

    handleSubmit = e => {
        e.preventDefault();
        // this.handleChange()
    };

    handleChange =({currentTarget: input}) => {
        const datas = {...this.state.datas}
        datas[input.name] = input.value
        this.setState({datas})
    };

    render() {
        const { datas } = this.state;
        return (
        <div>
           <div className="card shadow" style={{margin:80}}>
           <div className="card-header bg-info text-white">
                    <h4>To Do List App</h4>
                </div>
                <div className="card-body">
                    <div className="row">
                       <form onSubmit={this.handleSubmit}>
                       <div className="col-8">
                         <input onChange={(e)=>this.handleChange()} id="name" value={datas["name"]} name="name" type="text" className="form-control" placeholder="Add To do"/>
                        </div>
                        <div className="col-4">
                           <button className="btn btn-info" style={{width:100}}>Add</button>
                        </div>
                       </form>
                        <div className="col-12 mt-4">
                            <table className="table  table-hover">
                                <thead>
                                <tr>
                                    <th></th>
                                    <th></th>
                                </tr>
                                </thead>
                                <tbody>

                                    {this.state.datas.map( d => 
                                        <tr key={d.id}>
                                            <td>
                                                {d.name}
                                            </td>
                                            <td>
                                                <button onClick={()=> this.delete(d.id)} className="btn btn-danger">Delete</button>
                                            </td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <div className=" col-8">
                            <h5>Total Task: <span className="badge badge-info">{this.state.datas.filter(d=> d.status === true).length}</span></h5>
                        </div>
                        <div className=" col-4">
                            <h5>Complate Task: <span className="badge badge-info">{this.state.datas.filter( d => d.status === false ).length}</span></h5>
                        </div>
                    </div>
               </div>
            </div>
     </div>
      
        );
    }
}

export default ToDo;
