import React, {useState} from 'react';
import { ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'
  
function ToDo() {
    const [datas, setDatas] = useState([])
    const [data, setData] = useState({ 
        name:"", id:"", status:"" 
    });
    
    handleDelete = id => {
        const datas = datas.filter(d => d.id !== id);
        setDatas({datas});
    };
    
    complate=id=>{
    const index =  datas.indexOf(id);
    // console.log(index)
    const datas = [...datas]
    datas[index].status === true? datas[index].status = false: datas[index].status = true
    setDatat({datas});
    };

    handleSubmit = e => {
        e.preventDefault();
        const datas =[...datas]
        if(data.name !== ""){
        datas.push(data);
        this.setDatas({datas})
        } else toast.error('Please Enter the text')
    };
    
    handleChange = ({currentTarget: input}) => {
        const data = {...data}
        data[input.name] = input.value
        data['id'] = datas.length+1;
        data['status'] = true;
        setData({data})
    };

    handleChangeH1=() => {
        setDatas(datas);
        console.log(datas)
    }
    
        return (
            <div>
            <ToastContainer></ToastContainer>
           <div className="card shadow" style={{margin:20}}>
           <div className="card-header bg-info text-white">
                    <h4>To Do List App</h4>
                </div>
                <div className="card-body">
                       <form onSubmit={this.handleSubmit}>
                       <div className="row">
                        <div className="col-6">
                        <div className="form-group">
                            <input onChange={this.handleChange} name="name" value={data["name"]}  className="form-control" placeholder="Add To do"/>
                        </div>
                        </div>
                        <div className="col-4">
                           <button className="btn btn-info" style={{width:100}}>Add</button>
                        </div>
                       </div>
                       </form>
                       <div className="row">
                        <div className="col-12 mt-4">
                            <table  className="table  table-hover">
                                <thead>
                                <tr>
                                    <th></th>
                                    <th></th>
                                </tr>
                                </thead>
                                <tbody>

                                    {this.state.datas.map( d => 
                                        <tr key={d.id}>
                                            <td contentEditable="true">
                                                {d.status === true? <h5>{d.name}</h5>:<h5><del>{d.name}</del></h5> }
                                            </td>
                                            <td>
                                                <div className="">
                                                <button onClick={()=> this.delete(d.id)} className="m-1 btn btn-danger">Delete</button>
                                               {d.status === true?
                                               <button onClick={()=> this.complate(d)} className="btn btn-info">Complate</button>:
                                               <button onClick={()=> this.complate(d)} className="btn btn-info">Uncomplate</button>
                                               } 
                                                </div>
                                            </td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <div className=" col-8">
                            <h5>Total Task: <span className="badge badge-info">{this.state.datas.filter(d=> d.status === true).length}</span></h5>
                        </div>
                        <div className=" col-4">
                            <h5>Complate Task: <span className="badge badge-info">{this.state.datas.filter( d => d.status === false ).length}</span></h5>
                        </div>
                    </div>
               </div>
            </div>
     </div>
      
        );
    
}

export default ToDo;
