import React, {Component} from 'react';
  
class ToDo extends Component {
    
    state={ 
       datas:[],
       data:{ name:"", id:"", status:"" },
    };

    delete = (id) => {
        const datas = this.state.datas.filter(d => d.id !== id);
        this.setState({datas});
    };
    
    complate=id=>{
    const index =  this.state.datas.indexOf(id);
    // console.log(index)
    const datas = [...this.state.datas]
    datas[index].status === true? datas[index].status = false: datas[index].status = true
    this.setState({datas});
    };

    handleSubmit = e => {
        e.preventDefault();
        const datas =[...this.state.datas]
        if(this.state.data.name !== ""){
        datas.push(this.state.data);
        this.setState({datas})
        } else alert('Please Enter the text')
    };
    
    handleChange = ({currentTarget: input}) => {
        const data = {...this.state.data}
        data[input.name] = input.value
        data['id'] = this.state.datas.length+1;
        data['status'] = true;
        this.setState({data})
    };

    handleChangeH1=() => {
        this.setState(this.state.datas);
        console.log(this.state.datas)
    }
    render() {
        const { data } = this.state;
        return (
        <div>
           <div className="card shadow" style={{margin:20}}>
           <div className="card-header bg-info text-white">
                    <h4>To Do List App</h4>
                </div>
                <div className="card-body">
                       <form onSubmit={this.handleSubmit}>
                       <div className="row">
                        <div className="col-6">
                        <div className="form-group">
                            <input onChange={this.handleChange} name="name" value={data["name"]}  className="form-control" placeholder="Add To do"/>
                        </div>
                        </div>
                        <div className="col-4">
                           <button className="btn btn-info" style={{width:100}}>Add</button>
                        </div>
                       </div>
                       </form>
                       <div className="row">
                        <div className="col-12 mt-4">
                            <table  className="table  table-hover">
                                <thead>
                                <tr>
                                    <th></th>
                                    <th></th>
                                </tr>
                                </thead>
                                <tbody>

                                    {this.state.datas.map( d => 
                                        <tr key={d.id} contentEditable="true">
                                            <td>
                                                {d.status === true? <h5>{d.name}</h5>:<h5><del>{d.name}</del></h5> }
                                            </td>
                                            <td>
                                                <div className="">
                                                <button onClick={()=> this.delete(d.id)} className="m-1 btn btn-danger">Delete</button>
                                               {d.status === true?
                                               <button onClick={()=> this.complate(d)} className="btn btn-info">Complate</button>:
                                               <button onClick={()=> this.complate(d)} className="btn btn-info">Uncomplate</button>
                                               } 
                                                </div>
                                            </td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <div className=" col-8">
                            <h5>Total Task: <span className="badge badge-info">{this.state.datas.filter(d=> d.status === true).length}</span></h5>
                        </div>
                        <div className=" col-4">
                            <h5>Complate Task: <span className="badge badge-info">{this.state.datas.filter( d => d.status === false ).length}</span></h5>
                        </div>
                    </div>
               </div>
            </div>
     </div>
      
        );
    }
}

export default ToDo;
